import { Component } from '@angular/core';
import { MatButtonModule } from '@angular/material/button';
import { NgxGradientTextComponent } from '@omnedia/ngx-gradient-text';
import { NgxTypewriterComponent } from '@omnedia/ngx-typewriter';

@Component({
  selector: 'app-hero',
  standalone: true,
  imports: [MatButtonModule, NgxGradientTextComponent, NgxTypewriterComponent],
  templateUrl: './hero.component.html',
  styleUrl: './hero.component.scss',
})
export class HeroComponent {}
<div class="hero-container">
  <div class="content-overlay">
    <!-- <h1 class="mat-display-large">Fast. Reliable. Powerful.</h1> -->
    <om-gradient-text
      styleClass="mat-display-large"
      [text]="'Elevate Your Online Presence'"
    ></om-gradient-text>
    <om-typewriter
      [words]="[
        'Offering affordable and highly configurable websites to small and emerging businesses to expand their digital presence.'
      ]"
      [disableLoop]="true"
    ></om-typewriter>
    <button mat-fab extended>Get Started</button>
  </div>
</div>
