import { Component, EventEmitter, inject, Output } from '@angular/core';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatToolbarModule } from '@angular/material/toolbar';
import { BreakpointObserver, Breakpoints } from '@angular/cdk/layout';
import { Observable, map, shareReplay } from 'rxjs';
import { CommonModule } from '@angular/common';

interface MenuItem {
  icon: string;
  label: string;
  action?: string;
}

@Component({
  selector: 'app-header',
  standalone: true,
  imports: [MatToolbarModule, MatIconModule, MatButtonModule, CommonModule],
  templateUrl: './header.component.html',
  styleUrl: './header.component.scss',
})
export class HeaderComponent {
  private breakpointObserver = inject(BreakpointObserver);
  menuItems: MenuItem[] = [
    { icon: 'house', label: 'Home' },
    { icon: 'handshake', label: 'Services' },
    { icon: 'recent_actors', label: 'Portfolio' },
    { icon: 'comment', label: 'Contact', action: 'contact' },
  ];
  mobile$: Observable<boolean>;

  @Output() toggleDrawer = new EventEmitter<void>();

  constructor() {
    this.mobile$ = this.breakpointObserver
      .observe([Breakpoints.HandsetPortrait, Breakpoints.TabletPortrait])
      .pipe(
        map((result) => result.matches),
        shareReplay()
      );
  }

  onToggleDrawer() {
    this.toggleDrawer.emit();
  }
}
<mat-toolbar>
  <img class="header-logo" src="assets/svg/HeaderLogo-dark.svg" />
  <div class="header-button-container">
    <ng-container *ngIf="!(mobile$ | async); else mobile">
      <button mat-button>
        <mat-icon>house</mat-icon>
        Home
      </button>
      <button mat-button>
        <mat-icon>handshake</mat-icon>
        Services
      </button>
      <button mat-button>
        <mat-icon>recent_actors</mat-icon>
        Portfolio
      </button>
      <button mat-fab extended class="secondary">
        <mat-icon>comment</mat-icon>
        Contact
      </button>
    </ng-container>
    <ng-template #mobile>
      <button mat-mini-fab (click)="onToggleDrawer()">
        <mat-icon>menu</mat-icon>
      </button>
    </ng-template>
  </div>
</mat-toolbar>
