import { Component } from '@angular/core';
import { HeroComponent } from '@app/shared/components/hero/hero.component';
import { MatGridListModule } from '@angular/material/grid-list';
import { MatCardModule } from '@angular/material/card';
import { CommonModule } from '@angular/common';
import { NgxLampComponent } from '@omnedia/ngx-lamp';
import { MatButtonModule } from '@angular/material/button';

@Component({
  selector: 'app-home',
  standalone: true,
  imports: [
    HeroComponent,
    MatGridListModule,
    MatCardModule,
    CommonModule,
    NgxLampComponent,
    MatButtonModule,
  ],
  templateUrl: './home.component.html',
  styleUrl: './home.component.scss',
})
export class HomeComponent {
  services = [
    {
      name: 'Web UI Design',
      description:
        'Creating modern, user-friendly websites that are visually appealing and tailored to meet your business goals.',
    },
    {
      name: 'Photography',
      description:
        'Offering personalized photography services to help showcase your brand and enhance your online presence.',
    },
    {
      name: 'Hosting & Maintenance',
      description:
        'Providing reliable hosting and ongoing maintenance to ensure your website runs smoothly and stays up-to-date.',
    },
    {
      name: 'Website Visibility (SEO)',
      description:
        'Optimizing your website to improve search engine rankings and attract more potential customers to your business.',
    },
  ];

  breakpoint = 1;

  ngOnInit() {
    this.breakpoint = window.innerWidth <= 600 ? 1 : 2;
  }

  onResize(event: Event) {
    this.breakpoint = (event.target as Window).innerWidth <= 600 ? 1 : 2;
  }

  onResumeClick() {
    window.open('https://omnedia.dev/resume.pdf', '_blank');
  }
}
<app-hero></app-hero>

<div
  class="container"
  style="display: flex; flex-direction: column; align-items: center"
>
  <om-lamp backgroundColor="#1e1a1d" lampColor="#9a13cb"></om-lamp>

  <h2 style="margin-top: 1rem">About Me</h2>
  <p style="max-width: 1400px">
    I am a passionate developer dedicated to crafting beautiful and functional
    websites. I hold a degree in Computer Science and currently work as a
    Software Engineer at Rocket Mortgage, where I specialize in delivering
    innovative and user-focused solutions.
  </p>
  <button style="width: 50%" mat-fab extended (click)="onResumeClick()">
    Resumé
  </button>

  <h2 style="margin-top: 1rem">Services</h2>

  <mat-grid-list
    [cols]="breakpoint"
    rowHeight="10rem"
    (window:resize)="onResize($event)"
  >
    @for (service of services; track service) {
    <mat-grid-tile>
      <mat-card appearance="outlined" class="shrink-card">
        <mat-card-header>
          <mat-card-title>{{ service.name }}</mat-card-title>
        </mat-card-header>
        <mat-card-content>
          <p>{{ service.description }}</p>
        </mat-card-content>
      </mat-card>
    </mat-grid-tile>
    }
  </mat-grid-list>
</div>
