import { Component } from '@angular/core';
import { MatButton, MatButtonModule } from '@angular/material/button';

@Component({
  selector: 'app-hero',
  standalone: true,
  imports: [MatButtonModule],
  templateUrl: './hero.component.html',
  styleUrl: './hero.component.scss',
})
export class HeroComponent {}
<div class="hero-container">
  <div class="content-overlay">
    <h1 class="mat-display-large">Fast. Reliable. Powerful.</h1>
    <p>
      Offering affordable and highly configurable websites to small and emerging
      businesses. Let's build something together.
    </p>
    <button mat-fab extended>Let's Talk</button>
  </div>
</div>
