import { Component, EventEmitter, inject, Input, Output } from '@angular/core';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatToolbarModule } from '@angular/material/toolbar';
import { BreakpointObserver, Breakpoints } from '@angular/cdk/layout';
import { Observable, map, shareReplay } from 'rxjs';
import { CommonModule } from '@angular/common';
import { MenuItem } from '@app/data/interfaces/MenuItem';
import { Router, RouterModule } from '@angular/router';
import { CoreUtilitiesService } from '@app/data/services/utilities/core-utilities.service';
@Component({
  selector: 'app-header',
  standalone: true,
  imports: [
    MatToolbarModule,
    MatIconModule,
    MatButtonModule,
    CommonModule,
    RouterModule,
  ],
  templateUrl: './header.component.html',
  styleUrl: './header.component.scss',
})
export class HeaderComponent {
  private breakpointObserver = inject(BreakpointObserver);
  mobile$: Observable<boolean>;

  @Output() toggleDrawer = new EventEmitter<void>();
  @Input() menuItems: MenuItem[] = [];

  constructor(
    private router: Router,
    private coreUtilities: CoreUtilitiesService
  ) {
    this.mobile$ = this.breakpointObserver
      .observe([Breakpoints.HandsetPortrait, Breakpoints.TabletPortrait])
      .pipe(
        map((result) => result.matches),
        shareReplay()
      );
  }

  onToggleDrawer() {
    this.toggleDrawer.emit();
  }

  navigateTo() {
    this.coreUtilities.routeTo('/contact');
  }
}
<mat-toolbar class="custom-height">
  <img class="header-logo" src="assets/svg/HeaderLogo-dark.svg" />
  <ng-container *ngIf="!(mobile$ | async); else mobile">
    <div class="header-button-container">
      <ng-container *ngFor="let item of menuItems">
        <button mat-button [routerLink]="item.route">
          {{ item.label }}
        </button>
      </ng-container>
    </div>
    <button
      mat-fab
      extended
      class="secondary contact-button"
      (click)="onContactClick()"
    >
      <mat-icon>comment</mat-icon>
      Contact Me
    </button>
  </ng-container>
  <ng-template #mobile>
    <button mat-mini-fab (click)="onToggleDrawer()">
      <mat-icon>menu</mat-icon>
    </button>
  </ng-template>
</mat-toolbar>
